import React, { createContext, useState, useContext, useEffect } from 'react';
import { UserContext } from './UserContext';

export const DataContext = createContext();

export const DataProvider = ({ children }) => {
    const { user } = useContext(UserContext);
    const [data, setData] = useState(null);

    const getData = async () => {
      if (user) {
          try {
              const listsResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/lists/${user.uid}`)
              const listsJson = await listsResponse.json()
              const listsWithTasks = await Promise.all(
                  listsJson.map(async (list) => {
                      const tasksResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/tasks/${list.id}`)
                      const tasksJson = await tasksResponse.json();
                      return { ...list, tasks: tasksJson };
                  })
              );
              setData(listsWithTasks)
          } catch (error) {
              console.error(error)
          }
      } else {
          console.error('No user.')
      }
  };

  const getSharedLists = async () => {
        if (user) {
            try {
                const sharedListsResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/shared_lists/${user.uid}`);
                const sharedListsJson = await sharedListsResponse.json();
                // Assuming shared lists have similar structure to regular lists
                setData((prevData) => [...prevData, ...sharedListsJson]);
            } catch (error) {
                console.error('Error fetching shared lists:', error);
            }
        } else {
            console.error('No user.');
        }
    };

  useEffect(() => {
    getData();
    getSharedLists();
}, [user]); // Fetch data whenever user changes

  return (
    <DataContext.Provider value={{ data, getData }}>
      {children}
    </DataContext.Provider>
  );
};
