import React from 'react';
import { useEffect, useState, useContext } from 'react';
import TaskList from './components/TaskList';
import Header from './components/Header';
import AuthForm from './components/AuthForm';
import Sidebar from './components/Sidebar';
import './index.css';
import { DataContext } from './contexts/DataContext';
import { UserContext } from './contexts/UserContext';
import { AppContainer, CenteredContainer, ContentContainer } from './styles/Container.styled';

const App = () => {
  const { lists, sharedLists } = useContext(DataContext);
  const { user } = useContext(UserContext);
  const [selectedList, setSelectedList] = useState(null);

  return (
    <>
      {!user && 
      <CenteredContainer>
        <AuthForm />
      </CenteredContainer>
      }
      {user && (
        <AppContainer>
        <Sidebar lists={lists} sharedLists={sharedLists} setSelectedList={setSelectedList} />
        <ContentContainer>
          <Header />
          {selectedList ? (
            <TaskList key={selectedList.id} list={selectedList} tasks={selectedList.tasks} />
          ) : (
            <>
              <h1>Your Lists</h1>
              {lists && lists.length > 0 ? (
              lists.map((list) => <TaskList key={list.id} list={list} tasks={list.tasks} />)
              ) : (
                <p>No lists!</p>
              )}

              <h1>Lists shared with you</h1>
              {sharedLists && sharedLists.length > 0 ? (
              sharedLists.map((sharedList) => <TaskList key={sharedList.id} list={sharedList} tasks={sharedList.tasks} />)
              ) : (
                <p>No lists!</p>
              )}
            </>
          )}
        </ContentContainer>
      </AppContainer>
    )}
  </>
);
}

export default App;
