import styled from 'styled-components';

export const Form = styled.form`
    background-color: #eee;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 15px;
    padding-left: 15px;
`;

export const TextInput = styled.input`
    background-color: #eee;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 15px;
    margin: 0;
    width: 100%;
`;

export const ButtonInput = styled.input`
    background-color: white;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 15px;
    margin: 0;
    margin: 5px;
`;

export const TextContainer = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    gap: 5px;
    font-size: 15px;
`;
