import styled from 'styled-components';

export const ListTitleContainer = styled.div`
    display: flex;
    gap: 20px;
`;

export const ListTitle = styled.h1`
    font-weight: 600;
    font-size: 35px;
    margin: 0;
`;

export const ListDetails = styled.h2`
    font-weight: 600;
    font-size: 12px;
    margin: 0;
    font-weight: 400;
    color: #888796;
`;

export const TitleContainer = styled.div`
    display: flex;
    flex-direction: column;
    gap: 0;
    margin-top: 50px;
    margin-bottom: 50px;
`;

export const HeaderContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;;
    padding-top: 50px;
    padding-bottom: 50px;
`;

export const TaskListContainer = styled.div`
    padding-left: 30px;
`;

export const ListButton = styled.button`
    background-color: ${({ isSelected }) => isSelected ? '#ebf8fc' : 'transparent'};
    color: ${({ isSelected }) => isSelected ? '#2d7dfc' : '#575c6b'};
    border-radius: 30px;
    height: 30px;
    text-align: left;
    padding-left: 80px;
    border: none;
    font-size: 15px;
    font-weight: 600;

    &:hover {
        color: #2d7dfc;
    }

    &:active {
        color: #2d7dfc;
        background-color: #ebf8fc;
    }
`;

export const EditListButton = styled.button`
    background-color: transparent;
    border: none;
    font-size: 5rem;
`;

export const AddNewTaskButton = styled.button`

`;
