import styled from 'styled-components';

export const Overlay = styled.div`
    position: absolute;
    left: 0;
    top: 0;
    width: 100vw;
    height: 100vh;
    background-color: rgba(0,0,0,0.05);
    display: flex;
    justify-content: center;
    align-items: center;
`;

export const Modal = styled.div`
    width: 500px;
    background-color: white;
    padding: 40px;
    border-radius: 10px;
`;
