import React, { createContext, useState } from 'react';
import { UserContext } from './UserContext';

export const DataContext = createContext();

export const DataProvider = ({ children }) => {
    const { user } = useContext(UserContext);
    const [data, setData] = useState(null);

    const getData = async () => {
      if (user) {
        try {
          const listsResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/lists/${userID}`)
          const listsJson = await listsResponse.json()
          const listsWithTasks = await Promise.all(
              listsJson.map(async (list) => {
              const tasksResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/tasks/${list.id}`)
              const tasksJson = await tasksResponse.json();
              return { ...list, tasks: tasksJson};
              })
          );
          setData(listsWithTasks)
          } catch(error) {
          console.error(error)
          }
      } else {
        console.error('Error fetching user.')
      }
      };


  return (
    <DataContext.Provider value={{ data, getData }}>
      {children}
    </DataContext.Provider>
  );
};
