import React from 'react';
import TickBox from './TickBox';
import { useState, useContext, useEffect } from 'react';
import TaskModal from './TaskModal'
import { DataContext } from '../contexts/DataContext';
import { UserContext } from '../contexts/UserContext';
import { EditContainer, EditedBy, InfoContainer, Item, ItemContainer, TaskTitle } from '../styles/TaskListItem.styled';
import { UserIcon } from '../styles/ShareList.styled';

const TaskListItem = ({task}) => {
  const [showModal, setShowModal] = useState(false)
  const { getLists, getSharedLists } = useContext(DataContext);
  const { user } = useContext(UserContext);
  const [creatorData, setCreatorData] = useState({
    email: null,
    name: null
  });
  const [editorData, setEditorData] = useState({
    email: null,
    name: null
  });

  const fetchUserData = async () => {
    try {
      const creatorResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/users/${task.creator_id}`);
      if (creatorResponse.ok) {
        const userData = await creatorResponse.json();
        setCreatorData({ 
          email: userData.email, 
          name: userData.name 
        });
      } else {
        console.error('Error fetching creator user data:', creatorResponse.statusText);
      }
      if (task.last_edited_by) {
        const editorResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/users/${task.last_edited_by}`);
        if (editorResponse.ok) {
          const userData = await editorResponse.json();
          setEditorData({ 
            email: userData.email, 
            name: userData.name 
          });
        } else {
          console.error('Error fetching creator user data:', editorResponse.statusText);
        }
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, [task]);

  return (
    <Item>
      <ItemContainer>
        <TickBox task={task} />
        <InfoContainer>
          <TaskTitle>{task.title}</TaskTitle>
          {task.last_edited_by && editorData.name && <EditedBy>Last edited by {editorData.email} ({editorData.name})</EditedBy>}
        </InfoContainer>
      </ItemContainer>

      <EditContainer>
        {creatorData.name && creatorData.email && (
            <UserIcon 
            backgroundcolor='pink'
            textcolor='blue'
            email={creatorData.email}
            >
            { creatorData.name[0] } 
          </UserIcon>
        )}
        <button className="edit" onClick={() => setShowModal(true)}>EDIT</button>
      </EditContainer>
      {showModal && <TaskModal mode='edit' setShowModal={setShowModal} task={task}/>}
    </Item>
  );
}

export default TaskListItem;
