import React from 'react';
import { useContext } from 'react';
import ListModal from './ListModal';
import { useState } from 'react';
import { UserContext } from '../contexts/UserContext';

const Header = () => {
  const [showModal, setShowModal] = useState(false) // controls the state of the modal object
  const { user, logout } = useContext(UserContext);

  const handleLogout = async () => {
    console.log('Logging out')
    await logout();
    window.location.reload();
  };

  return (
    <div className="">
        <h2>Hello, welcome to my app :)</h2>
        <p>Welcome back, {user.displayName}</p>
        <div className="button-container">
            <button className="signout" onClick={handleLogout}>SIGN OUT</button>
            <button className="create" onClick={() => setShowModal(true)}>ADD NEW LIST</button>
        </div>
        {showModal && <ListModal mode='create' setShowModal={setShowModal} />}
    </div>
  );
}

export default Header;
