import React from 'react';
import { useState } from 'react';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, sendEmailVerification, updateProfile } from 'firebase/auth';
import { auth } from '../firebase';
import { AuthContainer } from '../styles/Container.styled';
import styled from 'styled-components';

const presetColors = [
  { value: '#f7bece', label: 'Red' },
  { value: '#ffe0bf', label: 'Orange' },
  { value: '#ccfab1', label: 'Green' },
  { value: '#ccffed', label: 'Turquoise' },
  { value: '#bbc1fc', label: 'Blue' },
  { value: '#f4d4ff', label: 'Purple' },
];

// Define styled components for the color options
const ColorButton = styled.button`
  background-color: ${({ color }) => color};
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: none;
  margin-right: 10px;
  cursor: pointer;

  &:hover {
    border: 1px solid grey;
  }
`;

const ColorButtonSelected = styled.button`
  background-color: ${({ color }) => color};
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 1px solid grey;
  margin-right: 10px;
  cursor: pointer;

  &:hover {
    border: 1px solid grey;
  }
`;

const AuthForm = () => {
  const [isLogIn, setIsLogIn] = useState(true);
  const [displayName, setDisplayName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState(null);
  const [selectedColor, setSelectedColor] = useState('');

  const viewLogIn = (status) => {
    setError(null);
    setIsLogIn(status);
  }

  const logIn = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log(userCredential);
      setError(null);
    } catch (error) {
      setError(error.message); // Set error message
      console.error(error);
    }
  }

  const signUp = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError('Make sure passwords match!')
      return;
    }

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      console.log(userCredential);

      // Update user's profile with display name
      await updateProfile(userCredential.user, {
        displayName: displayName,
      });

      // Send user data to backend
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/signup`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          id: userCredential.user.uid,
          email: email,
          name: displayName,
          color: selectedColor
        })
      });
      if (!response.ok) {
          throw new Error('Failed to create user.');
      }

      setError(null);

      // send email verification
      //await sendEmailVerification(auth.currentUser);

    } catch (error) {
      setError(error.message);
      console.error('Error signing up user:', error);
    }
  }

  return (
    <AuthContainer>
      <form onSubmit={isLogIn ? logIn : signUp}>
        {isLogIn ? <h1>Login</h1> : <h1>Sign up</h1>}
        {!isLogIn && <input type="text" placeholder="Enter your name" value={displayName} onChange={(e) => setDisplayName(e.target.value)}></input>}
        {!isLogIn && (
          <>
            <p>Select a color:</p>
            <div>
              {presetColors.map(color => (
                color.value === selectedColor ? ( 
                  <ColorButtonSelected
                    key={color.value}
                    color={color.value}
                    onClick={() => setSelectedColor(color.value)}
                    style={{ backgroundColor: color.value }}
                  />
                ) : (
                  <ColorButton
                  key={color.value}
                  color={color.value}
                  onClick={() => setSelectedColor(color.value)}
                  style={{ backgroundColor: color.value }}
                  />
                )
              ))}
            </div>
          </>
        )}
        <input type="email" placeholder="Enter your email" value={email} onChange={(e) => setEmail(e.target.value)}></input>
        <input type="password" placeholder="Enter your password" value={password} onChange={(e) => setPassword(e.target.value)}></input>'
        {!isLogIn && <input type="password" placeholder="Confirm your password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)}></input>}
        <button type='submit'>{isLogIn ? 'Log in' : 'Sign up'}</button>
        {error && <div>{error}</div>}
      </form>
      <div>
        <button onClick={() => viewLogIn(false)}>Sign Up</button>
        <button onClick={() => viewLogIn(true)}>Log in</button>
      </div>
    </AuthContainer>
  )
};

export default AuthForm;
