import React from 'react';
import TaskModal from './TaskModal';
import ListModal from './ListModal';
import { useState } from 'react';
import TaskListItem from './TaskListItem';

// TaskList displays all of the tasks in a given list
const TaskList = ({ listName, getData, tasks }) => {
  const [showTaskModal, setShowTaskModal] = useState(false) // controls the state of the task modal object
  const [showListModal, setShowListModal] = useState(false) // controls the state of the list modal object
  const sortedTasks = tasks?.sort((a,b) => new Date(a.date) - new Date(b.date)) // sorting the tasks according to date created

  return (
    <div className="">
        <h1>{ listName }</h1>
        {showListModal && <ListModal mode={'edit'} setShowModal={setShowListModal} getData={getData} list={'fake list'}/>}
        <div className="button-container">
            <button className="create" onClick={() => setShowTaskModal(true)}>ADD NEW TASK</button>
        </div>
        {sortedTasks?.map((task) => <TaskListItem key={task.id} task={task} getData={getData} />)}
        {showTaskModal && <TaskModal mode={'create'} setShowModal={setShowTaskModal} getData={getData} />}
    </div>
  );
}

export default TaskList;
