import React from 'react';
import { useContext } from 'react';
import ListModal from './ListModal';
import { useState } from 'react';
import { UserContext } from '../contexts/UserContext';
import { ButtonContainer, HeaderContainer } from '../styles/Container.styled';
import { SignOutButton } from '../styles/Button.styled';

const Header = () => {
  const [showModal, setShowModal] = useState(false) // controls the state of the modal object
  const { user, logout } = useContext(UserContext);

  const handleLogout = async () => {
    console.log('Logging out')
    logout();
  };

  return (
    <HeaderContainer>
        <p>Hello, {user.displayName}!</p>
        <ButtonContainer>
            <SignOutButton onClick={handleLogout}>SIGN OUT</SignOutButton>
            <button className="create" onClick={() => setShowModal(true)}>ADD NEW LIST</button>
        </ButtonContainer>
        {showModal && <ListModal mode='create' setShowModal={setShowModal} />}
    </HeaderContainer>
  );
}

export default Header;
