import React from 'react';
import { useState } from 'react';
import { useCookies } from 'react-cookie';

const TaskModal = ({ mode, setShowModal, task, list }) => {
    const editMode = mode === 'edit' ? true : false
    const [cookies, setCookie, removeCookie] = useCookies(null)

    const [data, setData] = useState({
        list_id: list.id,
        creator_user_email: editMode ? task.creator_user_email : cookies.Email,
        title: editMode ? task.title : "",
        date: editMode ? task.date : new Date()
    })

    const postData = async (e) => {
        e.preventDefault()
        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/todos`, {
                method: "POST",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(data)
            })
            if (res.status === 200) {
                console.log('worked!')
                setShowModal(false)
                getData()
            }
        } catch(e) {
            console.error(e)
        }
    }

    const editData = async(e) => {
        e.preventDefault()
        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/todos/${task.id}`, {
                method: "PUT",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(data)
            })
            if (res.status === 200) {
                console.log('worked!')
                setShowModal(false)
                getData()
            }
        } catch(e) {
            console.error(e)
        }
    }

    const handleChange = (e) => {
        console.log("changing!", e)
        const { value } = e.target

        setData(data => ({
            ...data,
            title: value
        }))
        console.log(data)
    }

    return (
        <div className="overlay">
            <div className="modal">
                <div className="form-title-container">
                    <h3>{mode} your task!</h3>
                    <button onClick={() => setShowModal(false)}>X</button>
                </div>

                <form>
                    <input 
                        required 
                        maxLength={30} 
                        placeholder="Task goes here" 
                        name="title"
                        value={data.title} 
                        onChange={handleChange}
                    />
                    <br />
                    <input type="submit" onClick={editMode ? editData : postData}/>
                </form>
            </div>
        </div>
    );
}

export default TaskModal;
