import styled from 'styled-components';

export const CheckboxContainer = styled.label`
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 40px;
  cursor: pointer;
  font-size: 22px;
  user-select: none;

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }
`;

export const Checkmark = styled.span`
  position: absolute;
  top: 0;
  left: 0;
  height: 15px;
  width: 15px;
  background-color: #fff;
  border-radius: 5px;
  border: 2px solid #eee;

  &:after {
    content: "";
    position: absolute;
    display: none;
    left: 4px;
    top: 1px;
    width: 4px;
    height: 8px;
    border: solid white;
    border-width: 0 3px 3px 0;
    transform: rotate(45deg);
  }

  ${CheckboxContainer}:hover input ~ & {
    background-color: #fff;
  }

  ${CheckboxContainer} input:checked ~ & {
    background-color: #2d7dfc;
    border: 2px solid #2d7dfc;

  }

  ${CheckboxContainer} input:checked ~ &:after {
    display: block;
  }
`;
