import React from 'react';
import { useEffect, useState } from 'react';
import TaskListItem from './components/TaskListItem';
import TaskList from './components/TaskList';
import Header from './components/Header';
import AuthForm from './components/AuthForm';
import './index.css';
import { useCookies } from 'react-cookie';

const App = () => {
  const [cookies, setCookie, removeCookie] = useCookies(null);
  const authToken = cookies.AuthToken;
  const userEmail = cookies.Email;
  const userID = cookies.UserID;
  const [ data, setData ] = useState(null);

  const getData = async () => {
    try {
      const listsResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/lists/${userID}`)
      const listsJson = await listsResponse.json()
      const listsWithTasks = await Promise.all(
        listsJson.map(async (list) => {
          const tasksResponse = await fetch(`${process.env.REACT_APP_SERVERURL}/tasks/${list.id}`)
          const tasksJson = await tasksResponse.json();
          return { ...list, tasks: tasksJson};
        })
      );
      setData(listsWithTasks)
    } catch(error) {
      console.error(error)
    }
  }

  useEffect(() => {
    if (authToken) {
      getData()
      console.log('data', data)
    }}, [authToken])

  return (
    <div className="app">
      {!authToken && <AuthForm />}
      {authToken &&
        <>
        <Header getData={getData} />
        {data?.map((list) => <TaskList key={list.id} list={list} getData={getData} tasks={list.tasks} />)}
        </>}
    </div>
  );
}

export default App;
