import React from 'react';
import styled from 'styled-components';
import TickBox from './TickBox';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRightLong } from '@fortawesome/free-solid-svg-icons';

const Container = styled.div`
    border-left: 3px solid ${({ color }) => color};
    border-radius: 3px;
    background-color: white;
    flex: 0 0 30%;
    box-shadow: 0px 0px 14px 1px rgba(0,0,0,0.02); 
`;

const TitleContainer = styled.div`
    background-color: ${({ color }) => color};
    width: 100%;
    color: white;
    border-radius: 0px 3px 0px 0px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const Title = styled.h1`
    margin: 0;
    padding-left: 20px;
    padding-top: 5px;
    padding-bottom: 5px;
    font-size: 16px;
    font-weight: 400;
`;

const Button = styled.button`
    margin-right: 10px;
    cursor: pointer;
    background-color: transparent;
    border: none;
    color: white;
`;

const ListInfo = styled.div`
    width: 100%;
    background-color: pink;
`;

const TaskList = styled.ul`
    list-style: none;
    padding: 0;
    margin-left: 10px;
`;

const TaskItem = styled.li`
    margin-bottom: 15px;
    display: flex;
    color: #292929;
    font-size: 14px;
`;

const ListContainer = ({ list, setSelectedList }) => {
    return (
        <Container color={list.color}>
            <TitleContainer color={list.color}>
                <Title>{list.title}</Title>
                <Button onClick={() => setSelectedList(list)}><FontAwesomeIcon icon={faArrowRightLong} /></Button>
            </TitleContainer>
            <TaskList>
                {list.tasks.map(task => (
                    <TaskItem key={task.id}>
                        <TickBox list={list} task={task} margin={'0px'}/>
                        {task.title}
                    </TaskItem>
                ))}
            </TaskList>
        </Container>
    );
}

export default ListContainer;
