import React from 'react';
import { useContext } from 'react';
import { UserContext } from '../contexts/UserContext';
import ListModal from './ListModal';
import { useState } from 'react';
import { DataContext } from '../contexts/DataContext';
import { SidebarContainer, SidebarListsContainer } from '../styles/Sidebar.styled.js';

const Sidebar = ({ setSelectedList }) => {
    const [showModal, setShowModal] = useState(false) // controls the state of the modal object
    const { lists, sharedLists } = useContext(DataContext);
  return (
    <>
    <SidebarContainer>
        <button className="create" onClick={() => setShowModal(true)}>ADD NEW LIST</button>
        <h1>Your Lists</h1>
        <SidebarListsContainer>
            {lists && lists.length > 0 ? (
            lists.map((list) => 
                <div key={list.id} onClick={() => setSelectedList(list)}>
                    { list.title }
                </div>
            )) : (
                <p>No lists!</p>
            )}
        </SidebarListsContainer>

        <h1>Shared Lists</h1>
        {sharedLists && sharedLists.length > 0 ? (
        sharedLists.map((sharedList) => 
            <div key={sharedList.id} onClick={() => setSelectedList(sharedList)}>
                { sharedList.title }
            </div>
        )) : (
            <p>No lists!</p>
        )}
    </SidebarContainer>
    {showModal && <ListModal mode='create' setShowModal={setShowModal} />}
    </>
  );
}

export default Sidebar;
