import React from 'react';
import ListModal from './ListModal';
import { useState } from 'react';
import { useCookies } from 'react-cookie';

const Header = () => {
  const [cookies, setCookie, removeCookie] = useCookies(null)
  const userEmail = cookies.Email
  const [showModal, setShowModal] = useState(false) // controls the state of the modal object

  const signOut = () => {
    console.log('signout')
    removeCookie('UserID')
    removeCookie('Email')
    removeCookie('AuthToken')
    window.location.reload()
  }

  return (
    <div className="">
        <h2>Hello, welcome to my app :)</h2>
        <p>Welcome back, {userEmail}</p>
        <div className="button-container">
            <button className="signout" onClick={signOut}>SIGN OUT</button>
            <button className="create" onClick={() => setShowModal(true)}>ADD NEW LIST</button>
        </div>
        {showModal && <ListModal mode={'create'} setShowModal={setShowModal} />}
    </div>
  );
}

export default Header;
