import React from 'react';
import { useState, useContext } from 'react';
import { CheckboxContainer, Checkmark } from '../styles/TickBox.styled';
import { DataContext } from '../contexts/DataContext';
import { UserContext } from '../contexts/UserContext';
import io from 'socket.io-client';

const socket = io('http://localhost:8000');

const TickBox = ({ list, task, margin }) => {
  const [isChecked, setIsChecked] = useState(task.completed);
  const { getLists, getSharedLists } = useContext(DataContext);
  const { user } = useContext(UserContext);

  useEffect(() => {
    socket.on('tasksUpdated', () => {
      console.log('Tasks updated in this list');
      // update state, perform actions here
      if (user.uid === list.owner_id) {
        getLists();
      } else {
        getSharedLists();
      }
    });

    // Clean up event listener
    return () => {
      socket.off('tasksUpdated');
    };
  }, []);


  const updateCompleted = async (e) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/tasks/${task.id}/completed`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ completed: !isChecked })
    });
      if (!response.ok) {
        throw new Error('Failed to update task');
      }
      setIsChecked(!isChecked);
      if (list.shared === true) {
          getSharedLists();
      } else {
          getLists();
      }
    } catch (error) {
      console.error('Error updating task', error);
    }
  };

  return (
    <CheckboxContainer margin={margin}>
      <input 
        type="checkbox" 
        checked={isChecked} 
        onChange={updateCompleted} 
      />
      <Checkmark />
    </CheckboxContainer>
  );
}

export default TickBox;
