import React from 'react';
import { useState } from 'react';
import { useCookies } from 'react-cookie';


const AuthForm = () => {
  const [cookies, setCookie] = useCookies(['Email', 'AuthToken']);
  const [isLogIn, setIsLogIn] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const viewLogIn = (status) => {
    setError('');
    setIsLogIn(status);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e, endpoint) => {
    e.preventDefault();
    setIsLoading(true);

    if (!isLogIn && formData.password !== formData.confirmPassword) {
      setError("Make sure passwords match!");
      setIsLoading(false);
      return;
    }

    try {
      const res = await fetch(`${process.env.REACT_APP_SERVERURL}/${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: formData.name, email: formData.email, password: formData.password }),
      });

      const data = await res.json();
      if (data.detail) {
        setError(data.detail);
      } else {
        setCookie('Email', data.email);
        setCookie('AuthToken', data.token);
        window.location.reload();
      }
    } catch (e) {
      console.error(e);
      setError('An error occurred. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-container-box">
        <form onSubmit={(e) => handleSubmit(e, isLogIn ? 'login' : 'signup')}>
          <h2>{isLogIn ? 'Please Log In' : 'Please Sign Up'}</h2>
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
          {!isLogIn && <input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} required />}
          <input type="submit" className="create" disabled={isLoading} value={isLoading ? 'Loading...' : isLogIn ? 'Log in' : 'Sign up'} />
          {error && <p className="error">{error}</p>}
        </form>
        <div>
          <button onClick={() => viewLogIn(false)}>Sign Up</button>
          <button onClick={() => viewLogIn(true)}>Log in</button>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
