import styled from 'styled-components';

export const CheckboxContainer = styled.div`
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 2px solid #000;
    position: relative;
    cursor: pointer;
`;

/** Hide default checkbox */
export const CheckboxInput = styled.input`
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
`;
