import styled from 'styled-components';

export const ShareListContainer = styled.div`
    display: flex;
    gap: 5px;
    align-items: center;
`;

export const ShareButton = styled.button`
    background-color: #efedf7;
    border: none;
    height: 30px;
    padding-left: 15px;
    padding-right: 15px;
    border-radius: 15px;
    margin-left: 10px;
    color: #312857;
    cursor: pointer;

    &:hover {
        background-color: #312857;
        color: white;
    }
`;

export const UserIcon = styled.div`
    position: relative;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-color: ${(props) => props.backgroundcolor};
    color: ${(props) => props.textcolor};
    display: flex;
    align-items: center;
    justify-content: center;
    text-transform: uppercase;
    cursor: default;

    &:hover::before {
        content: '${(props) => props.email}';
        position: absolute;
        top: -30px;
        background-color: grey;
        color: white;
        padding: 5px 10px;
        border-radius: 5px;
        z-index: 1;
    }

    &:hover {
        z-index: 2;
    }
`;
