import React from 'react';
import TaskModal from './TaskModal';
import ListModal from './ListModal';
import ShareListModal from './ShareListModal';
import { useState, useEffect } from 'react';
import TaskListItem from './TaskListItem';
import { UserContext } from '../contexts/UserContext';
import { useContext } from 'react';
import { getFirestore, doc, getDoc } from 'firebase/firestore';

// TaskList displays all of the tasks in a given list
const TaskList = ({ list, tasks }) => {
  const [showTaskModal, setShowTaskModal] = useState(false); // controls the state of the task modal object
  const [showListModal, setShowListModal] = useState(false); // controls the state of the list modal object
  const [showShareListModal, setShowShareListModal] = useState(false); // controls the state of the list modal object
  const { user } = useContext(UserContext);
  const [ownerData, setOwnerData] = useState({
    email: null,
    name: null
  });

  const fetchUserData = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/users/${list.owner_id}`);
      if (response.ok) {
        const userData = await response.json();
        setOwnerData({ 
          email: userData.email, 
          name: userData.name 
        });
      } else {
        console.error('Error fetching user data:', response.statusText);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, [list]);

  console.log(user.iud, list.owner_id);
  return (
    <div className="">
        <h1>{ list.title }</h1>
        <h2>Created by {ownerData.name} - {ownerData.email}</h2>
        <div className="button-container">
            {(list.owner_id === user.iud) && 
              <>
                <button className="edit" onClick={() => setShowShareListModal(true)}>SHARE LIST</button>
                <button className="edit" onClick={() => setShowListModal(true)}>EDIT LIST</button>
              </>
            }
            <button className="create" onClick={() => setShowTaskModal(true)}>ADD NEW TASK</button>
        </div>
        {tasks?.map((task) => <TaskListItem key={task.id} task={task} />)}
        {showTaskModal && <TaskModal mode={'create'} setShowModal={setShowTaskModal} list={list} />}
        {showListModal && <ListModal mode={'edit'} setShowModal={setShowListModal} list={list} />}
        {showShareListModal && <ShareListModal setShowModal={setShowShareListModal} list={list} />}
    </div>
  );
}

export default TaskList;
