import styled from 'styled-components';

export const CheckboxContainer = styled.div`
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 2px solid #000;
    position: relative;
    cursor: pointer;
`;

export const CheckboxInput = styled.input`
  position: absolute;
  opacity: 0;
  cursor: pointer;

  & + label {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 2px solid #000;
    position: relative;
    cursor: pointer;
  }

  & + label::after {
    content: "";
    width: 5px;
    height: 10px;
    border-width: 0 2px 2px 0;
    border-color: transparent;
    border-style: solid;
    position: absolute;
    top: 5px;
    left: 7px;
    transform: rotate(45deg);
    opacity: 0;
  }

  &:checked + label::after {
    opacity: 1;
  }
`;
