import styled from 'styled-components';

export const Form = styled.form`
    background-color: #eee;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 25px;
    padding-left: 15px;
`;

export const TextInput = styled.input`
    background-color: #eee;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 15px;
    margin: 0;
    width: 100%;
    padding-left: 5;

    &::placeholder {
        color: #969696;
    }
`;

export const ButtonInput = styled.input`
    background-color: ${({ color }) => color};
    color: white;
    display: flex;
    align-items: center;
    border: none;
    border-radius: 0px 25px 25px 0px;
    margin: 0;
    cursor: pointer;

    &:hover {
        background-color: white;
        color: ${({ color }) => color};
        border: 1px solid ${({ color }) => color};
    }
`;

export const TextContainer = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    gap: 5px;
    font-size: 15px;
    padding-left: 5px;
    color: #969696;
`;
